    private void jButtonCalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonCalculateActionPerformed
        // TODO add your handling code here:
        double num1 = Double.parseDouble(jTextField1.getText());
        double num2 = Double.parseDouble(jTextField2.getText());
        double result = 0.0;

        // Melakukan operasi berdasarkan tombol radio yang dipilih
        if (jRadioButton1.isSelected()) {
            result = num1 + num2;
        } else if (jRadioButton2.isSelected()) {
            result = num1 - num2;
        } else if (jRadioButton3.isSelected()) {
            result = num1 * num2;
        } else if (jRadioButton4.isSelected()) {
            result = num1 / num2;
        }

        // Menampilkan hasil
        jButtonCalculateActionPerformed.setText("Hasil: " + result);
    }//GEN-LAST:event_jButtonCalculateActionPerformed

