    private void jRadioButton11ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jRadioButton11ActionPerformed
        // TODO add your handling code here:
        double num1 = Double.parseDouble(jTextField3.getText());
        double num2 = Double.parseDouble(jTextField4.getText());
        String result = "";
        
        if (jRadioButton5.isSelected()) {
        result = num1 > num2 ? "True" : "False";
    } else if (jRadioButton6.isSelected()) {
        result = num1 < num2 ? "True" : "False";
    } else if (jRadioButton7.isSelected()) {
        result = num1 == num2 ? "True" : "False";
    } else if (jRadioButton8.isSelected()) {
        result = num1 != num2 ? "True" : "False";
    } else if (jRadioButton9.isSelected()) {
        result = num1 >= num2 ? "True" : "False";
    } else if (jRadioButton10.isSelected()) {
        result = num1 <= num2 ? "True" : "False";
    }

    // Menampilkan hasil
    jLabel9.setText("Hasil: " + result);
    }//GEN-LAST:event_jRadioButton11ActionPerformed

    private void jRadioButton12ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jRadioButton12ActionPerformed
        // TODO add your handling code here:
    double num1 = Double.parseDouble(jTextField5.getText());
    double num2 = Double.parseDouble(jTextField6.getText());
    String result1 = (num1 % 2 == 0) ? "Genap" : "Ganjil";
    String result2 = (num2 % 2 == 0) ? "Genap" : "Ganjil";

    // Menampilkan hasil
    jLabel12.setText("Hasil: Angka 1 " + result1 + " & Angka 2 " + result2);
    }//GEN-LAST:event_jRadioButton12ActionPerformed

    public String hitungNilaiUTS(double nilaiUTS) {
    String nilaiHuruf;
    if (nilaiUTS > 81) {
        nilaiHuruf = "A";
    } else if (nilaiUTS > 70) {
        nilaiHuruf = "B";
    } else if (nilaiUTS > 60) {
        nilaiHuruf = "C";
    } else if (nilaiUTS > 50) {
        nilaiHuruf = "D";
    } else {
        nilaiHuruf = "E";
    }
    return nilaiHuruf;
}

    
    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
      
    double nilaiUTS = Double.parseDouble(jTextField7.getText());
    String nilaiHuruf = hitungNilaiUTS(nilaiUTS);
    jLabel14.setText("Nilai UTS Anda: " + nilaiHuruf);
    }//GEN-LAST:event_jButton5ActionPerformed

